import React from 'react';

export default function Preloader() {
  const quotes = [
    { quote: "Omnia tempus habent", author: '' },
    { quote: 'Tempus edax rerum', author: '' },
    { quote: 'Tempus curat omnia', author: '' },
  ];

  const random = Math.floor(Math.random() * quotes.length);

  return (
    <div className="preloader space-y-2">
      <div className="w-40 text-center">
        {/* Кастомный спиннер с градиентом */}
        <div className="spinner mx-auto">
          <div className="double-bounce1"></div>
          <div className="double-bounce2"></div>
        </div>
        <style jsx>{`
          .spinner {
            width: 40px;
            height: 40px;
            position: relative;
          }
          .double-bounce1, .double-bounce2 {
            width: 100%;
            height: 100%;
            border-radius: 50%;
            background: linear-gradient(45deg, #d6336c, #ff6b6b);
            opacity: 0.6;
            position: absolute;
            top: 0;
            left: 0;
            animation: sk-bounce 2.0s infinite ease-in-out;
          }
          .double-bounce2 {
            animation-delay: -1.0s;
          }
          @keyframes sk-bounce {
            0%, 100% {
              transform: scale(0);
            } 50% {
              transform: scale(1);
            }
          }
        `}</style>
      </div>
      <div className="max-w-[400px] font-light text-gray-200">
        “{quotes[random].quote}”
      </div>
      <div className="max-w-[400px] text-sm font-light text-violet-800">
        {' '}
        - {quotes[random].author}
      </div>
    </div>
  );
}
