import React, { useRef } from 'react';
import { motion } from 'framer-motion';
import useOnScreen from './useOnScreen'; // Импортируйте ваш новый хук

const ProjectCard = ({ proj, index }) => {
  const ref = useRef(null);
  const isVisible = useOnScreen(ref);

  return (
    <motion.div
      ref={ref}
      className="space-y-4 bg-white p-4 shadow-md min-w-[300px] snap-start"
      key={`project${index}`}
      initial={{ y: 50, opacity: 0 }}
      animate={isVisible ? { y: 0, opacity: 1 } : {}}
      transition={{ duration: 0.5, delay: index * 0.1 }}
    >
      <div className="relative h-32 overflow-hidden border-b">
        <img alt="" src={proj.img} className="absolute left-0 my-auto w-full" />
      </div>
      <div className="text-2xl text-cyan-500">{proj.title}</div>
      <div className="h-20 overflow-y-auto text-xs font-light text-gray-400">
        {proj.details}
      </div>
      <div className="flex items-center justify-end space-x-4 opacity-40">
        {proj.links.map((link, linkIndex) => (
          <a
            href={link.link}
            target="_blank"
            rel="noreferrer"
            className="flex h-5 cursor-pointer items-center text-2xl text-gray-500"
            key={`project-link${linkIndex + proj.title}`}
          >
            {link.icon}
          </a>
        ))}
      </div>
    </motion.div>
  );
};

export default ProjectCard;
