import React, { useState } from 'react';

export default function Navbar() {
  const [showNav, setShowNav] = useState(false);

  function closeMobileNav() {
    setShowNav(false);
  }

  function toggleNav() {
    setShowNav(!showNav);
  }

  function closeMobileNavOnResize() {
    if (window.innerWidth > 700) {
      closeMobileNav();
    }
  }

  window.onresize = closeMobileNavOnResize;

  const navStyles = {
    container: {
      backgroundColor: '#000',
      color: '#fff',
      padding: '10px 20px',
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center'
    },
    profilePic: {
      borderRadius: '50%',
      overflow: 'hidden'
    },
    navToggler: {
      cursor: 'pointer',
      fontSize: '24px'
    },
    tabButton: {
      padding: '10px 20px',
      color: '#fff',
      textDecoration: 'none',
      position: 'relative',
      display: 'inline-block',
      transition: 'color 0.3s ease-in-out, transform 0.3s ease-in-out'
    },
    tabButtonHover: {
      color: '#ff6b6b',
      transform: 'translateY(-5px)'
    },
    tabButtonBefore: {
      content: '""',
      position: 'absolute',
      bottom: '0',
      left: '50%',
      width: '0',
      height: '2px',
      backgroundColor: '#ff6b6b',
      transition: 'width 0.3s ease-in-out, left 0.3s ease-in-out'
    },
    tabButtonHoverBefore: {
      width: '100%',
      left: '0'
    }
  };

  return (
    <div style={navStyles.container}>
      <div style={navStyles.profilePic}>
        <img src="{ProfilePic}" alt="" />
      </div>
      <div onClick={toggleNav} style={navStyles.navToggler}>
        <i className="fa fa-bars" aria-hidden="true"></i>
      </div>
      <div className={`tabs ${showNav && 'mobile-tabs'}`}>
        {['Главная', 'О себе', 'Навыки', 'Проекты'].map((item, index) => (
          <a
            key={index}
            onClick={closeMobileNav}
            href={`#${item.toLowerCase()}`}
            className="tab-button"
            style={navStyles.tabButton}
            onMouseEnter={(e) => {
              Object.assign(e.target.style, navStyles.tabButtonHover);
              Object.assign(e.target.style, navStyles.tabButtonHoverBefore);
            }}
            onMouseLeave={(e) => {
              Object.assign(e.target.style, navStyles.tabButton);
              Object.assign(e.target.style, navStyles.tabButtonBefore);
            }}
          >
            {item}
          </a>
        ))}
      </div>
    </div>
  );
}
