import React, { useState } from 'react';

export default function Navbar() {
  const [showNav, setShowNav] = useState(false);

  function closeMobileNav() {
    setShowNav(false);
  }

  function toggleNav() {
    setShowNav(!showNav);
  }

  function closeMobileNavOnResize() {
    if (window.innerWidth > 700) {
      closeMobileNav();
    }
  }

  window.onresize = closeMobileNavOnResize;

  const tabButtonStyle = {
    position: 'relative',
    display: 'inline-block',
    padding: '10px 20px',
    color: '#fff',
    textDecoration: 'none',
    transition: 'color 0.3s ease-in-out, transform 0.3s ease-in-out',
  };

  const tabButtonHoverStyle = {
    color: '#ff6b6b',
    transform: 'translateY(-5px)',
  };

  const tabButtonBeforeStyle = {
    content: '""',
    position: 'absolute',
    bottom: '0',
    left: '50%',
    width: '0',
    height: '2px',
    background: '#ff6b6b',
    transition: 'width 0.3s ease-in-out, left 0.3s ease-in-out',
  };

  const tabButtonHoverBeforeStyle = {
    width: '100%',
    left: '0',
  };

  return (
    <div className="topbar bg-black text-white">
      <div className="profile-pic">
        <img src="{ProfilePic}" alt="" />
      </div>
      <div onClick={toggleNav} className="nav-toggler">
        <i className="fa fa-bars" aria-hidden="true"></i>
      </div>
      <div className={`tabs ${showNav && 'mobile-tabs'}`}>
        <a
          onClick={closeMobileNav}
          href="#home"
          className="tab-button"
          style={tabButtonStyle}
          onMouseOver={(e) => {
            Object.assign(e.target.style, tabButtonHoverStyle);
            const beforeElement = document.createElement('span');
            Object.assign(beforeElement.style, tabButtonBeforeStyle, tabButtonHoverBeforeStyle);
            e.target.appendChild(beforeElement);
          }}
          onMouseOut={(e) => {
            Object.assign(e.target.style, tabButtonStyle);
            const beforeElement = e.target.querySelector('span');
            beforeElement && e.target.removeChild(beforeElement);
          }}
        >
          Главная
        </a>
        <a
          onClick={closeMobileNav}
          href="#about"
          className="tab-button"
          style={tabButtonStyle}
          onMouseOver={(e) => {
            Object.assign(e.target.style, tabButtonHoverStyle);
            const beforeElement = document.createElement('span');
            Object.assign(beforeElement.style, tabButtonBeforeStyle, tabButtonHoverBeforeStyle);
            e.target.appendChild(beforeElement);
          }}
          onMouseOut={(e) => {
            Object.assign(e.target.style, tabButtonStyle);
            const beforeElement = e.target.querySelector('span');
            beforeElement && e.target.removeChild(beforeElement);
          }}
        >
          О себе
        </a>
        <a
          onClick={closeMobileNav}
          href="#skills"
          className="tab-button"
          style={tabButtonStyle}
          onMouseOver={(e) => {
            Object.assign(e.target.style, tabButtonHoverStyle);
            const beforeElement = document.createElement('span');
            Object.assign(beforeElement.style, tabButtonBeforeStyle, tabButtonHoverBeforeStyle);
            e.target.appendChild(beforeElement);
          }}
          onMouseOut={(e) => {
            Object.assign(e.target.style, tabButtonStyle);
            const beforeElement = e.target.querySelector('span');
            beforeElement && e.target.removeChild(beforeElement);
          }}
        >
          Навыки
        </a>
        <a
          onClick={closeMobileNav}
          href="#projects"
          className="tab-button"
          style={tabButtonStyle}
          onMouseOver={(e) => {
            Object.assign(e.target.style, tabButtonHoverStyle);
            const beforeElement = document.createElement('span');
            Object.assign(beforeElement.style, tabButtonBeforeStyle, tabButtonHoverBeforeStyle);
            e.target.appendChild(beforeElement);
          }}
          onMouseOut={(e) => {
            Object.assign(e.target.style, tabButtonStyle);
            const beforeElement = e.target.querySelector('span');
            beforeElement && e.target.removeChild(beforeElement);
          }}
        >
          Проекты
        </a>
      </div>
    </div>
  );
}
