import React from 'react';
import PropTypes from 'prop-types';

function ProjectCard({ item }) {
  return (
    <>
      <div className="img-box w-[350px] lg2:w-auto mx-auto flex justify-center items-center relative overflow-hidden rounded-3xl mb-4"> {/* добавляем mb-4 */}
        <img src={item.img} alt="" className="w-full" />
        <div className="p absolute top-[-100%] left-0 h-full w-full text-center bg-[#b004b0b7] p-4 pt-20">
          <h1 className="font-bold text-3xl">{item.title}</h1>
          <p className="font-bold">{item.description}</p>
        </div>
      </div>
    </>
  );
}

ProjectCard.propTypes = {
  item: PropTypes.shape({
    img: PropTypes.string.isRequired,
    title: PropTypes.string.isRequired,
    description: PropTypes.string.isRequired,
  }).isRequired,
};

export default ProjectCard;
