import React, { useState } from 'react';

export default function Navbar() {
  const [showNav, setShowNav] = useState(false);

  function closeMobileNav() {
    setShowNav(false);
  }

  function toggleNav() {
    setShowNav(!showNav);
  }

  function closeMobileNavOnResize() {
    if (window.innerWidth > 700) {
      closeMobileNav();
    }
  }

  window.onresize = closeMobileNavOnResize;

  return (
    <div className="topbar bg-black text-white">
      <div className="profile-pic">
        <img src="{ProfilePic}" alt="" />
      </div>
      <div onClick={toggleNav} className="nav-toggler">
        <i className="fa fa-bars" aria-hidden="true"></i>
      </div>
      <div className={`tabs ${showNav && 'mobile-tabs'} flex items-center`}>
        <a onClick={closeMobileNav} href="#home" className="tab-button mx-2">
          Главная
        </a>
        <a onClick={closeMobileNav} href="#about" className="tab-button mx-2">
          О себе
        </a>
        <a onClick={closeMobileNav} href="#skills" className="tab-button mx-2">
          Навыки
        </a>
        <a onClick={closeMobileNav} href="#projects" className="tab-button mx-2">
          Проекты
        </a>
      </div>

      <style>{`
        .tab-button {
          position: relative;
          display: inline-block;
          padding: 10px 20px;
          color: #fff;
          text-decoration: none;
          border: 1px solid #ff6b6b;
          border-radius: 5px;
          transition: color 0.3s ease-in-out, transform 0.3s ease-in-out, border-color 0.3s ease-in-out;
        }

        .tab-button::before {
          content: "";
          position: absolute;
          bottom: 0;
          left: 50%;
          width: 0;
          height: 2px;
          background: #ff6b6b;
          transition: width 0.3s ease-in-out, left 0.3s ease-in-out;
        }

        .tab-button:hover {
          color: #ff6b6b;
          border-color: #fff;
          transform: translateY(-5px);
        }

        .tab-button:hover::before {
          width: 100%;
          left: 0;
        }
      `}</style>
    </div>
  );
}
