import React from 'react';
import ProjectCard from './ProjectCard'; // Импортируем новый компонент
import Xenia from '../assets/xenia.webp';
import Editorial from '../assets/editorial.webp';
import RebuildHub from '../assets/rebuild-hub.webp';
import Tradenza from '../assets/tradenza.webp';
import OJ from '../assets/oj.webp';

export default function Projects() {
  return (
    <div className="relative overflow-hidden bg-slate-50" id="projects">
      <div className="mx-auto max-w-6xl p-5 py-8 md:py-20">
        <div className="mb-8 md:mb-16 pb-4 text-6xl font-medium text-gray-300 md:text-left md:text-7xl">
          Проекты
        </div>
        <div className="flex overflow-x-auto space-x-4 snap-x snap-mandatory scrollbar">
          {projects.map((proj, index) => (
            <ProjectCard proj={proj} index={index} key={index} />
          ))}
        </div>
      </div>
    </div>
  );
}

const projects = [
  {
    title: 'Онлайн Судья',
    details:
      'Платформа для проведения кодинг-контестов с расчетом рейтингов в реальном времени на основе отправленных работ участников. Мы использовали ReactJS и Redux для разработки клиентской части проекта. Для серверной части мы использовали NodeJS, ExpressJS для разработки API, MongoDB как базу данных, Redis-Bull для поддержки очереди, docker для контейнеризации проекта и Judge0 в качестве движка выполнения кода.',
    img: OJ,
    links: [
      {
        icon: (
          <img
            alt=""
            src="https://img.icons8.com/ios-filled/50/000000/link--v1.png"
            className="h-full"
          />
        ),
        link: 'https://oj-client.vercel.app/',
      },
      {
        icon: <i className="devicon-github-original" />,
        link: 'https://github.com/PCSB-Web-Team/online-judge-server',
      },
    ],
  },
  {
    title: 'Вебсайт Xenia',
    details: 'Создание вебсайта с нуля для ежегодного мероприятия PCSB Xenia.',
    img: Xenia,
    links: [
      {
        icon: (
          <img
            alt=""
            src="https://img.icons8.com/ios-filled/50/000000/link--v1.png"
            className="h-full"
          />
        ),
        link: 'https://pcsbxenia.com/',
      },
    ],
  },
  {
    title: 'Rebuild Hub',
    details:
      'Веб-приложение, которое сократит разрыв между донорами отходов и коллекторами отходов, что положительно скажется на сборе перерабатываемых отходов.',
    img: RebuildHub,
    links: [
      {
        icon: (
          <img
            alt=""
            src="https://img.icons8.com/ios-filled/50/000000/link--v1.png"
            className="h-full"
          />
        ),
        link: 'https://rebuild-hub.netlify.app/',
      },
      {
        icon: <i className="devicon-github-original" />,
        link: 'https://github.com/Rebuild-Hub/Rebuild-Hub-Client',
      },
    ],
  },
  {
    title: 'Редакция',
    img: Editorial,
    details: 'Редакционный сайт PICT Debsoc.',
    links: [
      {
        icon: (
          <img
            alt=""
            src="https://img.icons8.com/ios-filled/50/000000/link--v1.png"
            className="h-full"
          />
        ),
        link: 'https://pictdebsoc.github.io/the_editorial/issue.html',
      },
      {
        icon: <i className="devicon-github-original" />,
        link: 'https://github.com/pictdebsoc/the_editorial',
      },
    ],
  },
  {
    title: 'Tradenza',
    details: 'Попытка создания платформы для проведения торгового контеста. Все еще в разработке.',
    img: Tradenza,
    links: [
      {
        icon: (
          <img
            alt=""
            src="https://img.icons8.com/ios-filled/50/000000/link--v1.png"
            className="h-full"
          />
        ),
        link: 'https://60f5901158dfc928b3aef62c--tradenza.netlify.app/',
      },
    ],
  },
];
